function statement(theFormData){
  var pronouns = ['he','she','they'];
  var possessives = ['his','hers','theirs'];
  var possessive = "his";
  var pronoun = "he";
  var html = "<div class='statement'>";
  var converters = {
    " I ": " "+pronoun+" ",
    "I ": pronoun+" ",
    " am ":" is ",
    "Because": "",
    " my ": " "+possessive+" ",
    "my ": possessive+" "
  }
  var media = localStorage["form-works-with"].split(", ");
  for(var i in theFormData){
    for(var j in converters){
      if(theFormData[i].indexOf(j) > -1)
        theFormData[i] = theFormData[i].replace(j,converters[j]);
    }
  }



  html +=
  theFormData["name"]+ " (b. "+
  theFormData["bday"]+". "+
  theFormData["birthplace"]+", "+
  theFormData["country"]+")" +" is a "+
  artistTitle(theFormData["works-with"])+ " who lives and works in "+
  theFormData["work-city"]+". He works in "+(media.length>1 ? "these media":"this medium")+" because "+
  ensurePeriod(theFormData["work-with"])+" He is inspired by the visual artist "+
  theFormData["artists"]+" because "+
  presentTense(theFormData["artists-2"]) + ". He is also inspired by the writer "+
  theFormData["writers"]+
  (theFormData["writers-2"].length>4 ? " because "+
  lower(theFormData["writers-2"]): "")+
  ". <br><br>The main subject of "+
  theFormData["name"]+"'s work is "+
  lower(theFormData["work-subject"])+" which is important to him because "+
  theFormData["work-subject-2"] +". His art-making process consists of "+
  ensurePeriod(lower(theFormData["process"]))+ " This is important to understanding his work because "+
  lower(theFormData["process-2"])+". He connected to the contemporary critical dialog of his medium through the ideas of the theorist "+
  theFormData["critical-dialogue"]+". The ideal exhibition space for his work is a "+
  (theFormData["exh-space-2"].length>10 ?
  lower(theFormData["exh-space"])+" because "+ensurePeriod(lower(theFormData["exh-space-2"])) :
  lower(theFormData["exh-space"])+". "
  )

  html += "<br><br>";
  // for(var i in theFormData){
    // html+= "<span class='statement-text'>"+theFormData[i]+"</span>";//$("[name='"+(i.replace("form-",""))+"']").val(localStorage[i]);
  // }
  html+= "</div>";
  return html;
}

function presentTense(text){
  var words = text.split(" ");
  words = words.map(function(e){
    if(verbs.indexOf(e)> -1){
      return e+"s";
    }
    return e;
  })
  return words.join(" ");
}

function removeI(text,pronoun){
  return text.replace()
}

var worksWithConverter = {
  "Painting": "painter",
  "Photography": "photographer",
  "Drawing": "illustrator",
  "Sculpture": "sculptor",
  "Performance": "performance artist",
  "Media art": "media artist",
  "Installation art": "Installation",
  "Film Video": "video artist",
  "Mixed media": "mixed media artist",
  "Bookarts": "book artist",
  "Text": "textual artist",
  "I work in a variety of media": "works in a variety of media"
}

function ensurePeriod(text){
  if(text.slice(text.length-2).indexOf(".")>-1)
    return text;
  return text+"."
}

function artistTitle(raw){
  var output = "";
  var types = raw.split(", ");
  types.map(function(e,i){
    // second to last
    if(i===types.length-2){
      output+= worksWithConverter[e]+", and ";
    }
    //last
    else if(i===types.length-1){
      output+= worksWithConverter[e]
    }
    else {
        output+= worksWithConverter[e]+", "+ (i===types.length-2 ? "and " : "");
    }

  })
  return output;
}


function lower(string) {
    return string.charAt(0).toLowerCase() + string.slice(1);
}


var selector = '.outsput';
var elements = document.querySelectorAll(selector);
$z = 1;

// Bind functions to events
for(var i = 0; i < elements.length; i++){
  elements[i].addEventListener('mousedown', drag);
  elements[i].addEventListener('mouseup', end);
};

// Drag function
function drag(event) {
  console.log("ok")
  // Set variable to true on mousedown
  $moving = true;
  // Increase z-index so last clicked always on top
  $z = $z+1;
  // Select the item that was clicked
  $this = event.target;
  // Positions cursor in center of element when being dragged, as oposed to the top left
  $width = $this.offsetWidth / 2;
  $height = $this.offsetHeight / 2;
  // Element follows mouse cursor
  document.addEventListener('mousemove',function(e) {
    // Only run if variable is true (this is destroyed on mouseup)
    if($moving === true){
      // Postion element, minus half width/height as above
      var x = e.clientX - $width;
      var y = e.clientY - $height;

      // Store left, top, and z-index in variable
      var position = 'left:' + x + 'px;top:' + y + 'px;z-index:'+$z+';cursor:move;';
      // Set style
      $this.setAttribute('style', position);
    };
  });
};

// Destroy drag on mouse up
function end() {
  console.log("ok")
  $moving = false;
};


window.statement = statement;
